import nltk
from nltk.corpus import wordnet as wn

def clean_text(text):
    parts = text.split('\n')
    #print(len(parts))
    #print(parts)
    final_text = ""
    for pt in parts:
        if len(pt)<2:
            continue
        tokens = nltk.word_tokenize(pt)
        if len(tokens)<3:
            continue
        found_tokens = 0
        for t in tokens:
            if len(wn.synsets(t))>=1:
                found_tokens = found_tokens + 1
        if float(found_tokens)/float(len(tokens))<0.5:
            continue
        final_text = final_text + "\n"+ pt
    return final_text



